/*======= TEAM ===============*/
.teamy {
  position: relative;
  z-index: 1;
  overflow: hidden;
}

.teamy_layout {
  position: relative;
}

.teamy_preview {
  position: relative;
  overflow: hidden;
}

.teamy_avatar {
  display: block;
  max-width: 100%;
  position: relative;
  z-index: 1;
}

.teamy_preview:before {
  content: "";
  position: absolute;
  top: 50%;
  left: 50%;
  z-index: 2;
}

.teamy_back {
  width: 100%;
  /* height: 100%;*/
  box-sizing: border-box;
  opacity: 0;
  will-change: opacity;
  display: flex;
  flex-direction: column;
  justify-content: center;
  position: absolute;
  top: 0;
  left: 0;
  z-index: 3;
}

.teamy:hover .teamy_back {
  opacity: 1;
  height: 100%;
  transition: opacity .4s cubic-bezier(0.71, 0.05, 0.29, 0.9) .2s;
}

.teamy_name {
  margin-top: 0;
  margin-bottom: 0;
}

.teamy_post {
  display: block;
}


/* TEAM common styles for visual styles */

.teamy_back {
  padding-left: 10px;
  padding-right: 10px;
  text-align: center;
}

.teamy_content {
  padding: 20px;
}

.teamy_name {
  font-size: 15px;
  text-transform: uppercase;
  letter-spacing: 1px;
  color: #282828;
  font-family: 'montserratbold';
}

.teamy_post {
  margin-top: .5em;
  font-size: .8em;
  text-transform: capitalize;
  color: #6f6f6f;
}

/* TEAM style 1 */

.teamy_style1 {
  text-align: center;
}

.teamy_style3 .teamy_name {
  font-size: 2em;
}


/* TEAM mask circle */

.teamy_mask-circle .teamy_preview:before {
  width: 0;
  height: 0;
  padding: 25%;
  border-radius: 50%;
  transition: transform .3s ease, opacity .3s ease-out;
  will-change: opacity, transform;
  opacity: 0;
  transform: translate(-50%, -50%) scale(0);
}

.teamy_mask-circle:hover .teamy_preview:before {
  opacity: 1;
  transform: translate(-50%, -50%) scale(4);
  transition-duration: .6s;
}

/* TEAM mask triangle */

.teamy_mask-triangle .teamy_preview:before {
  width: 100%;
  height: 100%;
  opacity: 0;
  -webkit-clip-path: polygon(50% 10%, 15% 90%, 85% 90%);
  clip-path: polygon(50% 10%, 15% 90%, 85% 90%);
  transition-property: transform, opacity;
  transition-duration: .2s, .4s;
  transition-delay: .4s, 0s;
  transition-timing-function: ease-out;
  will-change: transform, opacity;
  transform: translate(-50%, -50%) scale(1);
}

.teamy_mask-triangle:hover .teamy_preview:before {
  opacity: 1;
  transform: translate(-50%, -50%) scale(5);
  transition-delay: .1s, 0s;
  transition-duration: .4s;
}


/* TEAM zoom */

.teamy_zoom-photo .teamy_avatar {
  transition: transform .4s cubic-bezier(0.71, 0.05, 0.29, 0.9);
  will-change: transform;
  transform: scale(1);
}

.teamy_zoom-photo:hover .teamy_avatar {
  transform: scale(1.2);
}

/* TEAM zoom and rotate */

.teamy_zoom-rotate-photo .teamy_avatar {
  transition: transform .4s cubic-bezier(0.71, 0.05, 0.29, 0.9);
  will-change: transform;
  transform: scale(1) rotate(0);
}

.teamy_zoom-rotate-photo:hover .teamy_avatar {
  transform: scale(1.2) rotate(5deg);
}

/* TEAM zoom and slide */

.teamy_zoom-slide-photo .teamy_avatar {
  transition: transform .4s cubic-bezier(0.71, 0.05, 0.29, 0.9);
  will-change: transform;
  transform: scale(1) translate(0, 0);
}

.teamy_zoom-slide-photo:hover .teamy_avatar {
  transform: scale(1.2) translate(4%, 4%);
}

