@media (min-width: 992px) {
  :root{
    --title-font-size: 2.25rem;
    --subtitle-font-size: 1.125rem;
  }

  .container {
    max-width: 880px;
    margin-right: auto;
    margin-left: auto;
  }

  /* MENU */
  nav.show .menu {
    height: 50vh;
    width: 50vw;
    position: fixed;
    top: 0;
    right: 0;
    left: 50%;
    border-radius: 1rem;
  }

  nav.show ul.grid{
    gap: 2rem;
  }

  nav .menu ul li a.title {
    font: 400 1rem var(--body-font);
  }

  /* HOME */
  #home .container {
    grid-auto-flow: column;
    justify-content: space-between;
    margin: 0 auto;
  }

  #home .image {
    order: 1;
  }

  #home .text {
    order: 0;
    max-width: 18rem;
    text-align: left;
  }

  /* ABOUT */
  #about .container {
    margin: 0 auto;
    grid-auto-flow: column;
  }

  /* TESTIMONIALS */
  #testimonials .container {
    margin-left: auto;
    margin-right: auto;
  }
}

/* medium devices: > 767 */

@media (min-width: 768px) {
  .mobile-version {
    display: none;
  }
}

/* mobile devices: <= 767px */

@media (max-width: 767px) {

  .desktop-version {
    display: none;
  }

  .section header {
    margin-bottom: 0rem;
  }

  img {
    width: 80%; /* Reduces the image width to 70% of its original size, making it 30% smaller */
    height: auto; /* Maintains the aspect ratio of the image */
  }

  #home .image img {
    position: relative;
    right: 0rem;
  }

}

