  margin-left: 1rem;
  margin-right: 1rem;
  margin-bottom: 0;
}

#testimonials blockquote {
  margin-left: 2rem;
  margin-right: 2rem;
  padding: 2rem;
  box-shadow: 0px 3px 12px rgba(0, 0, 0, 0.25);
  border-radius: .8rem .8rem 0 0;
}

#testimonials blockquote p {
  position: relative;
  text-indent: 1.875rem; /* Indentation (paragraph space) */
  margin-bottom: 1.5rem;
  color: var(--title-color);
}

#testimonials blockquote p span {
  font: 700 3rem serif;
  position: absolute;
  top: -.5rem;
  left: -1.875rem;
  color: var(--base-color-alt);
}

#testimonials cite {
  display: flex;
  align-items: center;
  font-style: normal;
  color: var(--text-color);
}

#testimonials cite img {
  width: 4rem;
  height: 4rem;
  object-fit: cover; 
  clip-path: circle();
  margin-right: .5rem;
}

/*======= SWIPER ===============*/
.swiper-slide {
  height: auto;
  padding: 4rem 1rem;
}

.swiper-pagination-bullet {
  width: .75rem;
  height: .75rem;
}

.swiper-pagination-bullet-active {
  background: var(--base-color);
}

.swiper-button-next, .swiper-button-prev {
  color: var(--base-color-alt);
}

/*======= CONTACT ===============*/
#contact .grid {
  gap: 4rem;
}

#contact .text p {
  margin-bottom: 2rem;
}

#contact .button i, #contact ul li i {
  font-size: 1.5rem;
  margin-right: 0.625rem;
}

#contact ul.grid {
  gap: 2rem;
}

#contact ul li {
  display: flex;
  align-items: center;
}

#contact ul li i {
  color: var(--base-color);
}

/*======= FOOTER ===============*/
footer {
  background: var(--base-color);
}

footer.section {
  padding: 4rem 0;
}

footer .logo {
  display: inline-block;
  margin-bottom: 1.5rem;
}

footer .brand p {
  color: var(--text-color-light);
  margin-bottom: 0.75rem;
}

footer i {
  font-size: 1.5rem;
  color: var(--text-color-light);
}

footer .social a {
  margin-right: 2rem;
  transition: .3s;
  display: inline-block;
}

footer .social a:hover {
  transform: translateY(-0.3rem);
}

/*======= BACK TO TOP ===============*/

.back-to-top {
  background: var(--base-color);
  position: fixed;
  color: var(--text-color-light);

  right: 1rem;
  bottom: 1rem;
  padding: .8rem;

  clip-path: circle();

  font-size: 1.5rem;
  line-height: 0;

  visibility: hidden;
  opacity: 0;

  transition: .3s;
  transform: translateY(200%);
}

.back-to-top.show {
  visibility: visible;
  opacity: 1;
  transform: translateY(0);
}

/*==================================== extra-large devices: > 1200 */
@media (min-width: 1200px) {

  /* classes reutilizÃ¡veis */
  .container {
    max-width: 1120px;
    margin-right: auto;
    margin-left: auto;
  }

  .section {
    padding: 10rem 0;
  }

  .section header, #testimonials header {
    max-width: 40rem;
    text-align: center;
    margin-left: auto;
    margin-right: auto;
  }

  .button {
    height: 3.125rem;
  }

  /* NAVIGATION */
  nav .menu {
    opacity: 1;
    visibility: visible;
    top: 0;
  }

  nav .menu ul {
    display: flex;
    gap: 2rem;
  }

  nav .menu ul li a.title.active {
    font-weight: bold;
    -webkit-font-smoothing: auto;
  }

  nav .menu ul li a.title {
    font: 400 1rem var(--body-font);
  }

  nav .icon-menu {
    display: none;
  }

  /* LAYOUT */
  main {
    margin-top: var(--header-height);
  }

  /* HOME */
  #home .container {
    grid-auto-flow: column;
    justify-content: space-between;
    margin: 0 auto;
  }

  #home .image {
    order: 1;
  }

  #home .text {
    order: 0;
    max-width: 24rem;
    text-align: left;
  }

  /* ABOUT */
  #about .container {
    margin: 0 auto;
    grid-auto-flow: column;
  }

  /* SERVICES */
  .cards {
    grid-template-columns: 1fr 1fr 1fr;
  }

  .card {
    padding-left: 2rem;
    padding-right: 2rem;
  }

  /* TESTIMONIALS */
  #testimonials .container {
    margin-left: auto;
    margin-right: auto;
  }

  /* CONTACT */
  #contact .container {
    grid-auto-flow: column;
    align-items: center;
  }

  #contact .text {
    max-width: 25rem;
  }

  /* FOOTER */
  footer.section {
    padding: 3.75rem 0;
  }

  footer .container {
    grid-auto-flow: column;
    align-items: center;
    justify-content: space-between;
  }

  footer .logo {
    font-size: 2.25rem;
  }
}

/*====================================== large devices: > 992 */
@media (min-width: 992px) {
  :root{
    --title-font-size: 2.25rem;
    --subtitle-font-size: 1.125rem;
  }

  .container {
    max-width: 880px;
    margin-right: auto;
    margin-left: auto;
  }

  /* MENU */
  nav.show .menu {
    height: 50vh;
    width: 50vw;
    position: fixed;
    top: 0;
    right: 0;
    left: 50%;
    border-radius: 1rem;
  }

  nav.show ul.grid{
    gap: 2rem;
  }

  nav .menu ul li a.title {
    font: 400 1rem var(--body-font);
  }

  /* HOME */
  #home .container {
    grid-auto-flow: column;
    justify-content: space-between;
    margin: 0 auto;
  }

  #home .image {
    order: 1;
  }

  #home .text {
    order: 0;
    max-width: 18rem;
    text-align: left;
  }

  /* ABOUT */
  #about .container {
    margin: 0 auto;
    grid-auto-flow: column;
  }

  /* TESTIMONIALS */
  #testimonials .container {
    margin-left: auto;
    margin-right: auto;
  }
}

/* medium devices: > 767 */

@media (min-width: 768px) {
  .mobile-version {
    display: none;
  }
}

/* mobile devices: <= 767px */

@media (max-width: 767px) {

  .desktop-version {
    display: none;
  }

  .section header {
    margin-bottom: 0rem;
  }

  img {
    width: 80%; /* Reduces the image width to 70% of its original size, making it 30% smaller */
    height: auto; /* Maintains the aspect ratio of the image */
  }

  #home .image img {
    position: relative;
    right: 0rem;
  }

}
